import avatar2 from '../component/assets/images/users/avatar2.jpg';
import React, { Component } from 'react';
import { Serverurl, Serverurlimg } from '../Common/ServerUrl';
import axios from 'axios';
import $ from 'jquery';

export class GridRiders extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: [],
            rider:[]
        }
    }

    componentDidMount() {
        var data;

        axios({
            method: 'get',
            url: Serverurl + "user_show",
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(res => {
            console.log('res', res.data)
            console.log('hey', res.data)
            for (let i=0; i<res.data.length; i++){
                if(res.data[i].user_type == "rider"){
                    this.setState({
                        rider:[
                            ...this.state.rider,
                            res.data[i]
                        ]
                    })
                }
            }
            this.setState({
                data: res.data,
            })
            $(document).ready(function () {
                $('#datatable2').DataTable();
            });
            console.log('data', res.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log('err', err.response)
                console.log({ err })
            }
        })
    }

    delete(id) {
        localStorage.setItem('deleteEvent', id)
        console.log("idp", id)
    }

    deleteBooking() {
        // var id = localStorage.getItem("deleteEvent")

        // var Check = parseInt(id)

        // this.setState({
        //     GuideId: Check,
        
        // })

            var id = localStorage.getItem("deleteEvent")
        var data;

        axios({
            method: 'DELETE',
            url: Serverurl + 'brand_delete/' + id,
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        })

            .then((res) => {
                console.log('res', res.data)
                if (res.status === 'true') {
                    swal("Poof! Your imaginary file has been deleted!", {
                        icon: "success",
                    });
                    this.setState({
                        loading: true
                    })
                } else {
                    swal("Your Record Succesfully Deleted!");
                }
                setTimeout(() => {
                    window.location.reload()
                }, 2000)
            })

            .catch((err) => {
                console.log(err)
                if (err) {
                    setTimeout(() => {
                        this.setState({
                            loading: false
                        })
                        this.setState((willSuccess) => {
                            if (willSuccess) {
                                swal("Check Your Internet Connection", {
                                    icon: "warning",
                                });
                            } else {
                                swal("Your Record Succesfully Deleted!");
                            }
                        })
                    }, 5000)
                }
            })
    }


    


    // viewUser(id) {
    //     localStorage.setItem('buserId', id)
    //     console.log(id)
    //     window.location.href = "/component/ViewSystemBusinessUser"
    // }

    // updateUser(id) {
    //     localStorage.setItem('buserupdateId', id)
    //     console.log(id)
    //     window.location.href = "/component/UpdateBusinessUser"
    // }

    render() {
        return (
            <div>
                <div className="container">
                    <div class="row justify-content-center">
                        <div class="col-lg-11 col-sm-11">
                            <div class="card">
                                <div class="card-body table-responsive">
                                    <div className="list-head-btn">
                                        <div className="head">
                                            <h4>List Riders</h4>
                                        </div>
                                        <div class="button-align">
                                            <a href="/component/CreateRider" type="button" class="btn btn-danger waves-effect waves-light submit-button" >Add Riders</a>
                                        </div>
                                    </div>
                                    <div class="table-3">
                                        <table id="datatable2" class="table">
                                            <thead>
                                                <tr>
                                                    <th>Name</th>
                                                    <th>Email</th>
                                                    <th>Phone</th>
                                                    <th>Actions</th>

                                                </tr>
                                            </thead>


                                            <tbody>
                                                
                                            {this.state.rider.map((disease) =>
                                                    // {disease.user_type == "rider" ? 
                                                    <tr key={disease.id}>
                                                        <td>{disease.name}</td>
                                                        <td>{disease.email}</td>
                                                        <td>{disease.phone}</td>

                                                        <td>
                                                            <div class="icon-pad">
                                                                <a data-toggle="modal" data-target="#userEdit" onClick={this.delete}><i className="fas fa-trash-alt"></i></a>
                                                            </div>
                                                        </td>
                                                    </tr>
                                                    // : "null"}
                                                )}
                                                

                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="user-modal">
                        <div className="modal fade" id="userEdit" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                            <div className="modal-dialog" role="document">
                                <div className="modal-content">
                                    <div className="modal-header">
                                        <h5 className="modal-title" id="exampleModalLabel">Event Delete</h5>
                                        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                            <span aria-hidden="true">&times;</span>
                                        </button>
                                    </div>
                                    <div className="modal-body">
                                        <p>
                                            <strong>
                                                You're about to delete a Rider from this page. Are you sure?
                                            </strong>
                                        </p>
                                    </div>
                                    <div className="modal-footer">
                                        <button type="button" className="btn btn-secondary" data-dismiss="modal">Cancel</button>
                                        <button type="button" className="btn btn-primary" onClick={this.deleteBooking.bind(this)}>Delete</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default GridRiders
