    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
            errorText: ''
        })
    }

    imamBargah(e) {
        e.preventDefault();


        var validation = true;

        if (this.state.name == "") {
            validation = false;
            this.setState({
                errorText: "*Imam Bargah Name is required"
            })
        }

        // if (this.state.detail == "") {
        //     validation = false;
        //     this.setState({
        //         errorText: "*اسم الحملة مطلوب"
        //     })
        // }

        if (this.state.name_ur == "") {
            validation = false;
            this.setState({
                errorText: "*امام بارگاہ نام ضروری ہے"
            })
        }

        // if (this.state.detail_ur == "") {
        //     validation = false;
        //     this.setState({
        //         errorText: "*Campaign Status is required"
        //     })
        // }

        if (validation == true) {

            let formData = new FormData();
            formData.append('name', this.state.name)
            formData.append('name_ur', this.state.name_ur)
            // formData.append('banner_thumbnail', this.state.banner_thumbnail)

            // var data = {
            //     title: this.state.title,
            //     detail: this.state.detail,
            //     title_ur: this.state.title_ur,
            //     detail_ur: this.state.detail_ur
            // }
            this.setState({
                loading: true
            })

            // console.log('mydata', data)

            var error = document.getElementById('err');
            if (this.state.name === "" ||
                this.state.status === ""
            ) {
                this.setState({
                    error: 'Please fill fields carefully'
                })
                error.classList.add('errorMsg');
                setTimeout(() => {
                    error.classList.remove('errorMsg')
                    this.setState({
                        error: ''
                    })
                }, 3000)
            } else {
                axios({
                    method: 'post',
                    url: Serverurl + 'imam-bargah/create',
                    data: formData,
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`,
                    },
                    config: {
                        headers: { 'Content-Type': 'application/json' }
                    }
                })
                    .then((res) => {
                        console.log('res', res.data)
                        if (res.status === "true") {
                            swal("Poof! Your imaginary file has been deleted!", {
                                icon: "success",
                            });
                            this.setState({
                                loading: true
                            })
                        } else {
                            swal("Imam Bargah Succesfully Created!");
                        }
                        setTimeout(() => {
                            window.location.href = "/component/GridEventList"
                        }, 3000)
                        console.log('data', res.formData)
                    })
                    .catch((err) => {
                        console.log({ err })

                        console.log("res", err.response)

                        if (err) {
                            this.setState({
                                loading: true
                            })
                            setTimeout(() => {
                                this.setState({
                                    loading: false
                                })
                                this.setState((willSuccess) => {
                                    if (willSuccess) {
                                        swal("Something went wrong!", {
                                            icon: "warning",
                                        });
                                    } else {
                                        swal("Your imaginary file is safe!");
                                    }
                                })
                            }, 3000)
                        }
                    })
            }
        }
    }

