import React, { Component } from 'react';
import { CountryDropdown, RegionDropdown, CountryRegionData } from 'react-country-region-selector';
import { ServerurllogRegister, Serverurl } from '../Common/ServerUrl';
import axios from 'axios';
import swal from 'sweetalert';

export class CreateEvents extends Component {

    constructor(props) {
        super(props);
        this.state = {
            title: "",
            address: "",
            event_date_time: "",
            event_type_id: "",
            country_id: "",
            state_id: "",
            city_id: "",
            area_id: "",
            imam_bargah_id: "",
            manqabat_khuwan_id: "",
            noha_khuwan_id: "",
            zakir_id: "",
            islamic_date: "",
            // time: "",
            online_link: "",
            title_ur: "",
            address_ur: "",
            latitude: "",
            longitude: "",
            event_image:"",
            time_zone_name_id:"",

            ibg: [],
            mb: [],
            zak: [],
            area_cont: [],
            noha: [],
            CountryData: [],
            stateData: [],
            city_data: [],
            tz:[]

        };
    }

    componentDidMount() {
        var data;
        axios({
            method: 'get',
            url: Serverurl + 'imam-bargah/list',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(nes => {
            console.log('nes', nes.data.data)
            // console.log('hey', nes.data)
            this.setState({
                ibg: nes.data.data,
            })
            console.log('data', nes.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })

        var data;
        axios({
            method: 'get',
            url: Serverurl + 'time-zone/list',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(ues => {
            console.log('nes', ues.data.data)
            // console.log('hey', ues.data)
            this.setState({
                tz: ues.data.data,
            })
            console.log('data', ues.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })

        axios({
            method: 'get',
            url: Serverurl + 'manqabat-khuwan/list',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(mes => {
            console.log('mes', mes.data.data)
            // console.log('hey', mes.data)
            this.setState({
                mb: mes.data.data,
            })
            console.log('data', mes.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })

        axios({
            method: 'get',
            url: Serverurl + 'zakir/list',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(wes => {
            console.log('wes', wes.data.data)
            this.setState({
                zak: wes.data.data,
            })
            console.log('data', wes.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })

        axios({
            method: 'get',
            url: Serverurl + 'area/list',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(bes => {
            console.log('bes', bes.data.data)
            this.setState({
                area_cont: bes.data.data,
            })
            console.log('data', bes.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })

        //  GET COUNTRY

        axios({
            method: 'get',
            url: Serverurl + 'country/list',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(des => {
            console.log('des', des.data.data)
            this.setState({
                CountryData: des.data.data,
            })
            console.log('c-data', des.data.data)
            console.log('data-id', des.data.data.id)

        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })


        //GET STATE

        var Data = {
            "keyword": "sind"
        }
        axios({
            method: 'get',
            url: Serverurl + 'state/list',
            params: Data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(des => {
            console.log('stststst', des.data.data)
            this.setState({
                stateData: des.data.data,
            })
            console.log('state-data', des.data.data)

        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })



        //GET CITY
        var data = {
            "keyword": "karachi"
        }
        axios({
            method: 'get',
            url: Serverurl + 'city/list',
            params: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(tes => {
            console.log('des', tes.data.data)
            this.setState({
                city_data: tes.data.data,
            })
            console.log('data', tes.data.data)

        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })

        axios({
            method: 'get',
            url: Serverurl + 'noha-khuwan/list',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(yes => {
            console.log('des', yes.data.data)
            this.setState({
                noha: yes.data.data,
            })
            console.log('data', yes.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                console.log({ err })
            }
        })

    }





    handleChangeEvent(event) {
        this.setState({
            [event.target.name]: event.target.value,
            errorText: ""
        })
    }

    addMajlis(e) {

        var validation = true;


        if (this.state.event_image == "") {
            validation = false;
            this.setState({
                errorText: "*Event Image is required"
            })
        }

        if (this.state.time_zone_name_id == "") {
            validation = false;
            this.setState({
                errorText: "*Time Zone is required"
            })
        }

        if (this.state.imam_bargah_id == "") {
            validation = false;
            this.setState({
                errorText: "*Imam Bargah is required"
            })
        }

        if (this.state.zakir_id == "") {
            validation = false;
            this.setState({
                errorText: "*Zakir is required"
            })
        }

        // if (this.state.noha_khuwan_id == "") {
        //     validation = false;
        //     this.setState({
        //         errorText: "*Noha Khuwan is required"
        //     })
        // }

        if (this.state.title == "") {
            validation = false;
            this.setState({
                errorText: "*Title is required"
            })
        }

        if (this.state.country_id == "") {
            validation = false;
            this.setState({
                errorText: "*Country is required"
            })
        }

        if (this.state.state_id == "") {
            validation = false;
            this.setState({
                errorText: "*State is required"
            })
        }

        if (this.state.city_id == "") {
            validation = false;
            this.setState({
                errorText: "*City is required"
            })
        }

        // if (this.state.area_id == "") {
        //     validation = false;
        //     this.setState({
        //         errorText: "*Area is required"
        //     })
        // }

        if (this.state.event_date_time == "") {
            validation = false;
            this.setState({
                errorText: "*Date is required"
            })
        }

        if (this.state.address == "") {
            validation = false;
            this.setState({
                errorText: "*Address is required"
            })
        }

        if (this.state.islamic_date == "") {
            validation = false;
            this.setState({
                errorText: "*Islamic Date is required"
            })
        }

        // if (this.state.time == "") {
        //     validation = false;
        //     this.setState({
        //         errorText: "*Time is required"
        //     })
        // }

        if (this.state.latitude == "") {
            validation = false;
            this.setState({
                errorText: "*Latitude is required"
            })
        }

        if (this.state.longitude == "") {
            validation = false;
            this.setState({
                errorText: "*Longitude is required"
            })
        }

        if (this.state.online_link == "") {
            validation = false;
            this.setState({
                errorText: "*Online Link is required"
            })
        }

        if (this.state.title_ur == "") {
            validation = false;
            this.setState({
                errorText: "*Title Urdu is required"
            })
        }

        if (this.state.address_ur == "") {
            validation = false;
            this.setState({
                errorText: "*Address Urdu is required"
            })
        }



        if (validation == true) {

            let formData = new FormData();
            formData.append('event_image', this.state.event_image)
            formData.append('event_type_id', "1")
