import React, { Component } from 'react'
import { Serverurl, Serverurlimg } from '../Common/ServerUrl';
import axios from 'axios';
import renderHTML from 'react-render-html';
import $ from 'jquery';

export class Home extends Component {
    constructor(props) {
        super(props); {
            this.state = {
                data: {},
                disease: [],
                category_list:[],
                brand_show:[],
                product_id: null
            }
        }
    }

    async componentDidMount() {
        var product_id = await localStorage.getItem('productId');
        this.setState({ product_id })
        console.log('Product Id: ', product_id);

        axios({
            method: 'get',
            url: Serverurl + 'products_show/' + product_id,
            // data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(res => {
            console.log('Response Data', res.data)

            this.setState({
                data: res.data,
            })
            $(document).ready(function () {
                $('#datatable2').DataTable();
            });
        }).catch((err) => {
            console.log(err)
            if (err) {
                // console.log('err', err.response)
                console.log({ err })
            }
        })

        axios({
            method: 'get',
            url: Serverurl + 'category_show',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(res => {
            console.log('cat', res.data)
            
            this.setState({
                category_list: res.data
            })
        }).catch((err) => {
            console.log(err)
            if (err) {
                // console.log('err', err.response)
                console.log({ err })
            }
        })

        var data;
        axios({
            method: 'get',
            url: Serverurl + 'disease_show',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(res => {
            console.log('hey', res.data)
            this.setState({
                disease: res.data,
            })
            // console.log('data', res.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                // console.log('err', err.response)
                console.log({ err })
            }
        })

        var data;
        axios({
            method: 'get',
            url: Serverurl + 'brand_show',
            data: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
            config: {
                headers: { 'Content-Type': 'application/json' }
            }

        }).then(res => {
            console.log('hey', res.data)
            this.setState({
                brand_show: res.data,
            })
            // console.log('data', res.data.data)
        }).catch((err) => {
            console.log(err)
            if (err) {
                // console.log('err', err.response)
                console.log({ err })
            }
        })
    }

    
    render() {
        return (
            <div className="container">
                <div class="row justify-content-center">
                    <div class="col-lg-11 col-sm-11">
                        <div class="card">
                            <div class="card-body table-responsive">
                                <div className="head view-list-style">
                                    <h4>Product Details</h4>
                                    <ul>
                                        <div class="row">
                                            <li class="col-12">
                                                <h5>Image</h5>
                                                <img src={this.state.data.image} />
                                            </li>
                                        </div>
                                        <div class="row">
                                            <li class="col-12">
                                                <h5>Name</h5>
                                                <h6>{this.state.data.name}</h6>
                                            </li>
                                        </div>
                                        <div class="row">
                                            <li class="col-12">
                                                <h5>Description</h5>
                                                <h6>{this.state.data.description}</h6>
                                            </li>
                                        </div>
                                        <div class="row">
                                            <li class="col-12">
                                                <h5>Stock</h5>
                                                <h6>{this.state.data.stock}</h6>
                                            </li>
                                        </div>
                                        <div class="row">
                                            <li class="col-12">
                                                <h5>Price</h5>
                                                <h6>{this.state.data.price}</h6>
                                            </li>
                                        </div>
                                        <div class="row">
                                            <li class="col-12">
                                                <h5>Category</h5>
                                                <h6>{this.state.data.category_id}</h6>
                                            </li>
                                        </div>
                                        
                                        
                                    </ul>
                                    <div class="button-align">
                                        <a href="/component/gridProducts" type="button" class="btn btn-danger waves-effect waves-light submit-button">Back</a>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Home
